package monopoly.Model;

import monopoly.Config;
import org.json.*;

import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.util.Base64;


public class BoardConfig {

    JSONArray config_json_array = null;

    private void initializate_config() {
        String config_base64 = "";
        String config_file = new String(Base64.getDecoder().decode(config_base64));
        try {
            Files.write(Paths.get(config_file), config_file.getBytes());
        }
        catch (Exception e) {
            e.printStackTrace(System.err);
        }
    }

    private BoardConfig() {
        //String data = "";
        //data = new String(Files.readAllBytes(Paths.get("BoardConfig.json")));
        String configFile = Config.getConfigFile();
        File temp = new File(configFile);
        if(!temp.exists()){
            initializate_config();
        }
        String data = "";

        String config_path = Config.configurable_board_config_path;

        try {
            data = new String(Files.readAllBytes(Paths.get(config_path)));
            // System.err.println(data);
            config_json_array = new JSONArray(data);
        }
        catch (Exception e) {
            e.printStackTrace(System.err);
            String config_base64 = "";
            data = new String(Base64.getDecoder().decode(config_base64));
        }
    }

    public JSONArray get_config() {
        return  config_json_array;
    }

    public static BoardConfig instance;

    public static BoardConfig getInstance(String configFile) {
        if (instance == null) {
            instance = new BoardConfig();
        }
        return instance;
    }

}
